namespace Flaeng.Productivity.Tests;

public class SourceBuilderTests
{

    [Fact]
    public void Can_make_empty_source()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable
                     """, result);
    }

    [Fact]
    public void Can_change_tab_size()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.TabLength = 2;
        sourceBuilder.StartNamespace("Test");
        sourceBuilder.StartClass(new ClassOptions("TestClass"));
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace Test
                     {
                       public class TestClass
                       {
                       }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_use_tabs()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.TabLength = 1;
        sourceBuilder.TabStyle = TabStyle.Tabs;
        sourceBuilder.StartNamespace("Test");
        sourceBuilder.StartClass(new ClassOptions("TestClass"));
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace Test
                     {
                     
                     """ +
                     '\t' + "public class TestClass" + Environment.NewLine +
                     '\t' + "{" + Environment.NewLine +
                     '\t' + "}" + Environment.NewLine +
                     """
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_disable_nullable()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.NullableEnable = false;
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable disable
                     """, result);
    }

    [Fact]
    public void Can_make_empty_namespace()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_class()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartClass(new ClassOptions("Publicy"));
        sourceBuilder.StartMethod(new MethodOptions("string", "Parse")
        {
            Visibility = MemberVisiblity.Private,
            Static = true
        });
        sourceBuilder.AddLineOfCode("return \"\";");
        sourceBuilder.EndMethod();
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         public class Publicy
                         {
                             private static string Parse()
                             {
                                 return "";
                             }
                         }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_static_class()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartClass(new ClassOptions("Publicy")
        {
            Visibility = TypeVisiblity.Public,
            Static = true,
            Interfaces = new[] { "BaseClass", "Interface1", "Interface2" }
        });
        sourceBuilder.StartMethod(new MethodOptions("string", "Parse")
        {
            Visibility = MemberVisiblity.Private,
            Static = true
        });
        sourceBuilder.AddLineOfCode("return \"\";");
        sourceBuilder.EndMethod();
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         public static class Publicy : BaseClass, Interface1, Interface2
                         {
                             private static string Parse()
                             {
                                 return "";
                             }
                         }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_interface()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartInterface(new InterfaceOptions("Interconnected")
        {
            Visibility = TypeVisiblity.Internal,
            Partial = true,
            Interfaces = new[] { "BaseInterface" }
        });
        sourceBuilder.AddMethodStub(new MethodOptions("void", "Invoke")
        {
            Parameters = new List<string>(new[] { "object obj", "string str" })
        });
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         internal partial interface Interconnected : BaseInterface
                         {
                             void Invoke(
                                 object obj,
                                 string str
                                 );
                         }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_struct()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartStruct(new StructOptions("DataContainer")
        {
            Visibility = TypeVisiblity.Public,
            Partial = true
        });
        sourceBuilder.AddProperty(new PropertyOptions("string", "Name")
        {
            Visibility = MemberVisiblity.Protected
        });
        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         public partial struct DataContainer
                         {
                             protected string Name { get; set; }
                         }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_properties()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartClass(new ClassOptions("Publicy")
        {
            Visibility = TypeVisiblity.Public
        });

        sourceBuilder.AddProperty(new PropertyOptions("string", "Text")
        {
            Visibility = MemberVisiblity.Public,
            Getter = GetterSetterVisiblity.Protected,
            Setter = GetterSetterVisiblity.Private,
            DefaultValue = "\"\""
        });

        sourceBuilder.AddProperty(new PropertyOptions("string", "Text2")
        {
            Visibility = MemberVisiblity.Public,
            Getter = GetterSetterVisiblity.Private,
            Setter = GetterSetterVisiblity.Protected
        });

        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         public class Publicy
                         {
                             public string Text { protected get; private set; } = "";
                             public string Text2 { private get; protected set; }
                         }
                     }
                     
                     """, result);
    }

    [Fact]
    public void Can_make_fields()
    {
        // Arrange
        var sourceBuilder = new SourceBuilder();

        // Act
        sourceBuilder.StartNamespace("SpacyName");
        sourceBuilder.StartClass(new ClassOptions("Publicy")
        {
            Visibility = TypeVisiblity.Public
        });

        sourceBuilder.AddField(new FieldOptions("string", "Text")
        {
            Visibility = MemberVisiblity.Public,
            DefaultValue = "\"\""
        });

        sourceBuilder.AddField(new FieldOptions("string", "Text2")
        {
            Visibility = MemberVisiblity.Public
        });

        sourceBuilder.EndClass();
        sourceBuilder.EndNamespace();
        var result = sourceBuilder.ToString();

        // Assert
        Assert.Equal("""
                     // <auto-generated/>

                     #nullable enable

                     namespace SpacyName
                     {
                         public class Publicy
                         {
                             public string Text = "";
                             public string Text2;
                         }
                     }
                     
                     """, result);
    }

}
